select g.id, g.goods_name, g.description, g.origin, gs.fxqty as stock, gf.path, gi.item_id from goods g left join goods_item gi on g.goods_id = gi.goods_id left join goods_stock gs on gs.item_id = gi.item_id left join goods_file gf on g.goods_id = gf.goods_id where gi.item_id in
#{item}
group by g.goods_id,gi.item_id

select agr.id, agr.status, agr.init_price, agr.floor_price, DATE_FORMAT(agr.create_time,'%Y-%m-%d %H:%i:%s') as create_time, agr.update_time, agr.user_id, agp.id as recordId, DATE_FORMAT(agp.create_time,'%Y-%m-%d %H:%i:%s') as sub_create_time, agp.user_id as sub_user_id, agp.user_name, agp.user_img, agp.bargain_price, agp.buy, agro.duration, agro.item_id from activity_goods_record agr left join activity_goods_participant agp on agr.id = agp.goods_record_id left join activity_goods_role agro on agr.goods_role_id = agro.id where agr.id = #{id}
and agr.user_id = #{userId}
and agp.user_id = #{userId}

select * from activity_goods_role 

	where id not in 
	
		#{item}

select goods_role_id as id ,count(*) as count from activity_goods_record where goods_role_id in 

	#{item}

group by goods_role_id
